{"untitled.rb"=>"\ndef answer number\n  number == 0 ? \"zero\" : \"one\"\nend\n", "untitled_spec.rb"=>"require './untitled'\n\ndescribe \"untitled\" do\n  \n  context \"when numbers are under 10\" do\n    it \"spells zero for 0\" do\n      answer(0).should == \"zero\"\n    end\n\n    it \"spells one for 1\" do\n      answer(1).should == \"one\"\n    end\n  end  \n\nend", "cyber-dojo.sh"=>"# Test output can be formatted as progress or documentation\nrspec . --format progress", "instructions"=>"Spell out a number. For example\n\n      99 --> ninety nine\n     300 --> three hundred\n     310 --> three hundred and ten\n    1501 --> one thousand, five hundred and one\n   12609 --> twelve thousand, six hundred and nine\n  512607 --> five hundred and twelve thousand,\n             six hundred and seven\n43112603 --> forty three million, one hundred and\n             twelve thousand,\n             six hundred and three\n\n[Source http://rosettacode.org]", "output"=>"..\n\nFinished in 0.00048 seconds\n2 examples, 0 failures\n"}
