{"humanizer.rb"=>"module Humanizer\n  @@grammar = {0 => \"zero\", 1 => \"one\", 2 => \"two\", 3 => \"three\", 4 => \"four\", \n               5 => \"five\", 6 => \"six\", 7 => \"seven\", 8 => \"eight\", 9 => \"nine\",\n               10 => \"ten\", 11 => \"eleven\", 12 => \"twelve\", 13 => \"thirteen\", 14 => \"fourteen\", 15 => \"fifteen\", \n               20 => \"twenty\", 30 => \"thirty\", 40 => \"forty\", 50 => \"fifty\", 80 => \"eighty\"}\n\n  def self.spell number\n    return @@grammar[number] unless @@grammar[number].nil?\n\n    if (number >= 1000000)\n      spell_million number\n    elsif (number >= 1000)\n      spell_thousand number\n    elsif (number >= 100)\n      spell_hundred number\n    elsif (number > 20)\n      spell_dozen number\n    else\n      spell_teen number\n    end\n  end\n\n  def self.spell_teen number\n    spell(number - 10) + \"teen\"\n  end\n\n  def self.spell_dozen number\n    result = @@grammar[round_down_to(10, number)] || (spell(number / 10) + \"ty\")\n    result += \" \" + spell(number % 10) unless factor_of? 10, number\n    result\n  end\n\n  def self.spell_hundred number\n    result = spell(number / 100) + \" hundred\"\n    result += \" and \" + spell(number % 100) unless factor_of? 100, number\n    result\n  end\n\n  def self.spell_thousand number\n    result = spell(number / 1000) + \" thousand\"\n    result += \", \" + spell(number % 1000) unless factor_of? 1000, number\n    result\n  end\n\n  def self.spell_million number\n    result = spell(number / 1000000) + \" million\"\n    result += \", \" + spell(number % 1000000) unless factor_of? 1000000, number\n    result\n  end\n\n  def self.factor_of? factor, number\n    number % factor == 0\n  end\n\n  def self.round_down_to factor, number\n    number - (number % factor)\n  end\nend", "humanizer_spec.rb"=>"require './humanizer'\n\ndescribe \"untitled\" do\n  shared_examples_for \"number humanizer\" do |number, words|\n    it \"should spell \#{words} for \#{number}\" do\n      Humanizer::spell(number).should == words\n    end\n  end\n\n  context \"when numbers are under 10\" do\n    it_behaves_like \"number humanizer\", 0, \"zero\"\n    it_behaves_like \"number humanizer\", 1, \"one\"\n    it_behaves_like \"number humanizer\", 9, \"nine\"\n  end\n\n  context \"when numbers are under sixteen\" do\n    it_behaves_like \"number humanizer\", 10, \"ten\"\n    it_behaves_like \"number humanizer\", 11, \"eleven\"\n    it_behaves_like \"number humanizer\", 12, \"twelve\"\n    it_behaves_like \"number humanizer\", 13, \"thirteen\"\n    it_behaves_like \"number humanizer\", 14, \"fourteen\"\n    it_behaves_like \"number humanizer\", 15, \"fifteen\"\n  end\n\n  context \"when numbers from 16 through to 19\" do\n    it \"should add teen to number\" do\n      (16..19).each do |n|\n        Humanizer::spell(n).should == Humanizer::spell(n - 10) + \"teen\"\n      end\n    end\n  end  \n\n  context \"when round numbers under 100\" do\n    it_behaves_like \"number humanizer\", 20, \"twenty\"\n    it_behaves_like \"number humanizer\", 30, \"thirty\"\n    it_behaves_like \"number humanizer\", 40, \"forty\"\n    it_behaves_like \"number humanizer\", 50, \"fifty\"\n    it_behaves_like \"number humanizer\", 80, \"eighty\"\n\n    it \"should add ty to number\" do\n      (60..90).step(10) do |n|\n        next if n == 80 \n        Humanizer::spell(n).should == Humanizer::spell(n/10) + \"ty\"\n      end\n    end\n  end\n\n  context \"when unrounded numbers under 100\" do\n    it \"should spell dozens followed by spelled digits\" do\n      (21..99).step(11) do |n|\n        Humanizer::spell(n).should == Humanizer::spell(n/10*10) + \" \" + Humanizer::spell(n%10)\n      end\n    end\n  end\n\n  context \"when rounded hundreds\" do\n    it_behaves_like \"number humanizer\", 100, \"one hundred\"\n    it_behaves_like \"number humanizer\", 200, \"two hundred\"\n    it_behaves_like \"number humanizer\", 900, \"nine hundred\"\n  end\n\n  context \"when unrounded numbers under thousand\" do\n    it \"should spell hundreds followed by spelled dozens followed by spelled digits\" do\n      (101..999).step(111) do |n|\n        Humanizer::spell(n).should == Humanizer::spell(n/100*100) + \" and \" + Humanizer::spell(n%100)\n      end\n    end\n  end\n\n  context \"when numbers above thousand\" do\n    it_behaves_like \"number humanizer\", 1000, \"one thousand\"\n    it_behaves_like \"number humanizer\", 1501, \"one thousand, five hundred and one\"\n    it_behaves_like \"number humanizer\", 12609, \"twelve thousand, six hundred and nine\"\n    it_behaves_like \"number humanizer\", 512607, \"five hundred and twelve thousand, six hundred and seven\"\n  end\n\n  context \"when numbers above million\" do\n    it_behaves_like \"number humanizer\", 1000000, \"one million\"\n    it_behaves_like \"number humanizer\", 43112603, \"forty three million, one hundred and twelve thousand, six hundred and three\"\n  end\nend", "cyber-dojo.sh"=>"# Test output can be formatted as progress or documentation\nrspec . --format progress", "instructions"=>"Spell out a number. For example\n\n      99 --> ninety nine\n     300 --> three hundred\n     310 --> three hundred and ten\n    1501 --> one thousand, five hundred and one\n   12609 --> twelve thousand, six hundred and nine\n  512607 --> five hundred and twelve thousand,\n             six hundred and seven\n43112603 --> forty three million, one hundred and\n             twelve thousand,\n             six hundred and three\n\n[Source http://rosettacode.org]", "output"=>"...........................\n\nFinished in 0.0155 seconds\n27 examples, 0 failures\n"}
